import { useEffect, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import { increase, decrease } from "../stor/Counter";
export default function Counter() {
  const globalState = useSelector((state) => state);

  const dispatch = useDispatch();

  const state = useSelector((state) => {
    if (state.value < 1) {
      return "no number";
    }
    return state.value;
  });

  const counterOperation = useCallback(
    (type, payload) => {
      dispatch({ type, payload });
    },
    [dispatch]
  );

  useEffect(() => {
    counterOperation("increase", 1);
  }, [counterOperation]);

  return (
    <div className="App">
      <h1>Hello Redux Basic</h1>
      <div className="counter">Counter: {globalState.value}</div>;
      <div>
        <button onClick={() => dispatch(increase(2))} className="btn">
          increase +
        </button>
        <button onClick={() => dispatch(decrease(2))} className="btn">
          decrease -
        </button>
      </div>
      <div>
        <button className="btn">Hide/Show Counter Number</button>
      </div>
    </div>
  );
}
