import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import "./App.css";

function App() {
  const dispatch = useDispatch();
  const state = useSelector((state) => {
    if (state.value < 1) {
      return "no number";
    }
    return state.value;
  });

  const counterOperation = (type, payload) => {
    dispatch({ type, payload });
  };

  return (
    <div className="App">
      <h1>Hello Redux Basic</h1>
      <div className="counter">Counter: {state}</div>;
      <div>
        <button onClick={() => counterOperation("increase", 1)} className="btn">
          increase +
        </button>
        <button onClick={() => counterOperation("decrease", 1)} className="btn">
          decrease -
        </button>
      </div>
      <div>
        <button className="btn">Hide/Show Counter Number</button>
      </div>
    </div>
  );
}

export default App;
